package com.day2;

public class ArithematicOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println("addition operator (+) :"+(a+b));
		System.out.println("subtraction operator (-) :"+(a-b));
		System.out.println("multiplication operator (*) :"+(a*b));
		System.out.println("division operator (/) :"+(b/a));
		System.out.println("mod operator (%) :"+(b%a));
		System.out.println("xor operator (^) :"+(a^b));
	}

}
