package com.day2;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		a=a+1;
		System.out.println("a="+a);
		a=a-1;
		System.out.println("a="+a);
		a=a*1;
		System.out.println("a="+a);
		a=a/1;
		System.out.println("a="+a);
		a=a%1;
		System.out.println("a="+a);
	}

}
