package com.day2;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=53;
		int b=34;
		System.out.println("right shift:");
		System.out.println(a>>b);
		System.out.println("left shift:");
		System.out.println(a<<b);
	}

}
