package com.day4;

public class One_Dimensional_Array {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]=new int[5];
		a[0]=455;
		a[1]=234;
		a[2]=200;
		a[3]=24;
		a[4]=45;
		System.out.println("array elements are :");
		for(int i=0;i<a.length;i++) {
			System.out.println(a[i]);
		}
	}

}
